customForm.ref:
prefix bibo: <http://purl.org/ontology/bibo/>
prefix dcterms: <http://purl.org/dc/terms/>
prefix foaf: <http://xmlns.com/foaf/0.1/>
prefix coda: <http://art.uniroma2.it/coda/contracts/>

rule it.riccardosacco.bibobibtex.BookForm id:bookform {
    nodes = {
        book uri(coda:randIdGen("book", {})) .
        title literal userPrompt/title .
        isbn13 literal userPrompt/isbn13 .
        isbn10 literal userPrompt/isbn10 .
        publisher literal userPrompt/publisher .
        year literal userPrompt/year .
        edition literal userPrompt/edition .
    }
    graph = {
        $book a bibo:Book .
        $book dcterms:title $title .
        OPTIONAL { $book bibo:isbn13 $isbn13 . }
        OPTIONAL { $book bibo:isbn10 $isbn10 . }
        OPTIONAL { $book dcterms:publisher $publisher . }
        OPTIONAL { $book dcterms:issued $year . }
        OPTIONAL { $book bibo:edition $edition . }
    }
}
