customForm.ref:
prefix foaf: <http://xmlns.com/foaf/0.1/>
prefix coda: <http://art.uniroma2.it/coda/contracts/>

rule it.riccardosacco.bibobibtex.ContributorForm id:contributorform {
    nodes = {
        person uri(coda:randIdGen("person", {})) .
        givenName literal userPrompt/givenName .
        familyName literal userPrompt/familyName .
    }
    graph = {
        $person a foaf:Person .
        $person foaf:givenName $givenName .
        $person foaf:familyName $familyName .
    }
}
